#' @export
#' @md
CreateExternalDataStoresVerifySQL <- function(password, maxRows, dataStoreId, query, credentialId, user, useKerberos = FALSE, ...) {
  dataStoreSqlVerify <- datarobot.apicore::DataStoreSQLVerify$new(password = password, maxRows = maxRows, query = query, credentialId = credentialId, user = user, useKerberos = useKerberos, validateParams = TRUE)
  return(datarobot.apicore::DataConnectivityApi$new()$ExternalDataStoresVerifySQLCreate(dataStoreSqlVerify = dataStoreSqlVerify, dataStoreId = dataStoreId, ...))
}

